<?php

namespace AppBundle\Repository;

/**
 * EnPresentationRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class EnPresentationRepository extends \Doctrine\ORM\EntityRepository
{
    /**
     * Liste e
     */
    public function findPresentation($slug, $limit, $offset)
    {
        return $this->createQueryBuilder('p')
                    ->join('p.type', 't')
                    ->where('t.slug LIKE :slug')
                    ->andWhere('p.statut = 1')
                    ->andWhere('t.statut = 1')
                    ->setFirstResult($offset)
                    ->setMaxResults($limit)
                    ->setParameter('slug', '%'.$slug.'%')
                    ->getQuery()->getResult()
        ;
    }

    /**
     * L'article concerne par le type
     */
    public function findPresentationByType($type)
    {
        return $this->createQueryBuilder('p')
                    ->join('p.type', 't')
                    ->where('t.slug LIKE :slug')
                    ->andWhere('p.statut = 1')
                    ->setParameter('slug',  '%'.$type.'%')
                    ->getQuery()->getOneOrNullResult()
        ;
    }
}
